const constantCase = require('constant-case');

module.exports = ({ paths: { cwdDir }, actions: { names, defaultNames } }) => {
  const actionNames = names || defaultNames;
  const constantNames = actionNames.map(constantCase);
  return `import { ${constantNames.join(', ')} } from '../';
${names ? ``: `
// ðŸ”¥ these action types tests are generated by default, you want your own types here`}
describe('\`${cwdDir}\` action types', () => {${constantNames.map(constantName => `
  it('should export \`${constantName}\` type', () => {
    expect(${constantName}).toBe('${constantName}');
  });`).join('\n')}
});
`;
}
